package com.kushtal.zcodec

import java.security._
import java.security.spec._
import java.util.Base64

object CipherKeys {
  private val algorithm: String = "RSA"
  private val publicKeyVal = "MIGfMA0GCSqGSIb3DQEBAQUAA4GNADCBiQKBgQCgFGVfrY4jQSoZQWWygZ83roKXWD4YeT2x2p41dGkPixe73rT2IW04glagN2vgoZoHuOPqa5and6kAmK2ujmCHu6D1auJhE2tXP+yLkpSiYMQucDKmCsWMnW9XlC5K7OSL77TXXcfvTvyZcjObEz6LIBRzs6+FqpFbUO9SJEfh6wIDAQAB"
  private val privateKeyVal = ""

  val publicKey: PublicKey = getPublicKey(publicKeyVal)
  val privateKey: PrivateKey = getPrivateKey(privateKeyVal)

  private def getPrivateKey(base64PrivateKey: String): PrivateKey = {
    val keySpec = new PKCS8EncodedKeySpec(Base64.getDecoder.decode(base64PrivateKey.getBytes))
    val keyFactory = KeyFactory.getInstance(algorithm)
    keyFactory.generatePrivate(keySpec)
  }

  private def getPublicKey(base64PublicKey: String): PublicKey = {
    val keySpec = new X509EncodedKeySpec(Base64.getDecoder.decode(base64PublicKey.getBytes))
    val keyFactory = KeyFactory.getInstance(algorithm)
    keyFactory.generatePublic(keySpec)
  }
}
